      this.scene.input.on( "pointerup", this.onStageInput, this );


      // Initialize properties from parameters.
      this.help_menu = ( typeof instanceProperties[ "help-menu" ] !== "undefined" ) ? this.scene.getChildById( instanceProperties[ "help-menu" ], true ) : null;


      // Boot phase.
      this.camera = this.scene.cameras.main;


    }

    // BUILT-IN METHODS

    preUpdate () {

    }

    update () {

    }

    postUpdate () {

    }

    destroy () {
      this.owner.off( "levelSwitch", this.destroy, this );

      // Detach custom event listeners.
      this.scene.input.off( "pointerdown", this.onPointerDown2, this );
      this.scene.input.off( "pointerup", this.onStageInput, this );

    }

    // CUSTOM METHODS

    onStageInput () {
      // Executed whenever the stage is pressed.
      this.help_menu = this.scene.getChildrenByTag( 'help-menu' )[ 0 ];
      if (null != this.help_menu) {
        if ((this.errorCheckNotNull( this.help_menu, this.owner, "`Get Visibility of Instance` block could not find an instance named [help_menu].")).visible) {
          if ( !this.help_menu ) {
            this.reportError( "`Set Instance Visibility` block could not find the instance [help_menu]." );
            return;
          }
          this.help_menu.visible = false;
        } else {
          if (this.instContains( this.owner, (this.scene.input.manager.mousePointer.x + this.camera.posX), (this.scene.input.manager.mousePointer.y + this.camera.posY) )) {
            if ( !this.help_menu ) {
              this.reportError( "`Set Instance Visibility` block could not find the instance [help_menu]." );
              return;
            }
            this.help_menu.visible = true;
          }
        }
      }
      this.owner.playMy( 'idle' );
    }

    onPointerDown2 ( pointer ) {
      if ( !this.owner || !this.owner.exists ) {
        return;
      }
      var point = this.camera.getWorldPoint( pointer.x, pointer.y );
      if ( this.owner.getBounds().contains( point.x, point.y ) ) {
      this.owner.playMy( 'hover' );

      }
    }

    reportError( message ) {
      message = "(" + this.name.replace( /_[\d]+$/, "" ) + ")" + message;
      console.trace( message );
      this.game.reportError( message, message, "SCRIPT ERROR" );
    }

    errorCheckNotNull( input, backup, message ) {
      if( !input ) {
        this.reportError( message );
        return backup;
      }
      return input;
    }

    instContains ( ins, x, y ) {
      if ( !ins || !ins.getHitbox ) return false;
      var hitbox = ins.getHitbox();
      return hitbox.contains( x, y );
    }

  }
);

