      this.onUpdate();

    }

    postUpdate () {

    }

    destroy () {
      this.owner.off( "levelSwitch", this.destroy, this );

      // Detach custom event listeners.
      this.owner.removeListener( this.owner.EVENTS.LEVEL_START, this.onLevelStart2, this );

    }

    // CUSTOM METHODS

    onCreate () {
      // Executed when this script is initially created.
      this.owner.setPhysics( false );
      this.owner.tags.add( 'end-point' );
      this.getPlayerOffStage(  );
    }

    onUpdate () {
      // Executed every frame.
      if (this.scene.time.now - this.List_Refresh_Time > this.Refresh_Delay_Time) {
        this.getPlayerOffStage(  );
      }
      if (this.Player != null) {
        if (this.overlapping( this.owner, this.Player )) {
          this.complete(  );
        }
      }
    }

    onLevelStart2() {
      this.getPlayerOffStage(  );

    }

    getPlayerOffStage (  ) {
      this.Player = this.scene.getChildrenByTag( this.Player_Tag )[ 0 ];
      this.List_Refresh_Time = this.scene.time.now;
    }

    overlapping ( ins1, ins2 ) {
      // Get the most appropriate hitboxes and compare them.
      if( !ins1 || !ins2 || !ins1.getHitbox || !ins2.getHitbox ) {
        return false;
      }
      var box1 = ins1.getHitbox();
      var box2 = ins2.getHitbox();
      var intersection = Phaser.Geom.Rectangle.Intersection( box1, box2 );
      return !!( intersection.width && intersection.height );
    }

    complete (  ) {
      if (this.Active) {
        this.game.levelManager.switchTo( ( this.game.levelManager.levels.indexOf( this.scene ) + 1 ) + 1 );
      }
    }

  }
);

